// MAP SIZE
const map = document.querySelector('#map')
const grow1 = document.querySelector('.mas1')
const grow2 = document.querySelector('.mas2')
const shrink = document.querySelector('.menos')
let size = 70

grow1.addEventListener('click', () => {
    map.style.height = (size + 10) +'vh';
    size += 10
})

grow2.addEventListener('click', () => {
    map.style.height = (size + 10) +'vh';
    size += 10
})

shrink.addEventListener('click', () => {
    map.style.height = (size - 10) +'vh';
    size -= 10
})